import React, { useState, useEffect } from "react"
import {Switch, Route} from "react-router-dom";
import axios from "axios"
import Recipedetails from "./Recipedetails"
import { useHistory } from "react-router-dom";


function Recipieslist(props){ 
    let history = useHistory();
    const [ recipeDetails , setRecipeDetails ] = useState({})
    const {recipeList} = props

    useEffect(() => { 
        history.push("/details")
    }, [recipeDetails])

    function getDetails(e, id){ 
        e.preventDefault()
        axios({
            "method":"GET",
            "url": `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=false&apiKey=3cada9b92e194b29af52f3e43a75c988`,
            "headers":{
            "content-type":"application/json",
            // "x-rapidapi-host":"spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
            // "x-rapidapi-key":"bb0f200289mshe78ca82c1ce41f8p10d1a9jsn884393783c99"
            }
            })
            .then(res => setRecipeDetails(() => ({ 
                recipeDetails: res.data
            })))
            .then(recipeDetails !== null ? history.push("/details") : console.log("its null"))
            .catch(err => console.log(err))



        }


    return ( 
        <>
        {recipeList.map((item) => 
            <div className = "main-home"> 
                <img key = {item.id} alt = "image not found" src ={`https://spoonacular.com/recipeImages/${item.id}-636x393.jpg`}></img>
                <h1 key = {item.title} className = "recp-title">{item.title}</h1>
                <h4 key = {item.readyInMinutes} className = "recp-sub-title">Ready in {item.readyInMinutes} minutes</h4>
                <h4 key = {item.servings} className = "recp-sub-title">Serves {item.servings}</h4>
                <button onClick = {(e) => {
                    getDetails(e, item.id) 

                    }}>See More</button>
                    <Switch>
                        <Route exact path = "/details">
                            <Recipedetails recipeDetails = {recipeDetails}/>
                        </Route>
                </Switch>
            </div>
                )}
        </>

    )
}



export default Recipieslist
