import React, { useState } from "react"
import {Switch, Route} from "react-router-dom";
import axios from "axios"
import Recipedetails from "./Recipedetails"
import { useHistory } from "react-router-dom";


function Recipieslist(props){ 

    const [ recipeDetails , setRecipeDetails ] = useState([])
    const {recipeList} = props

    function getDetails(e){ 
        e.preventDefault()
        axios({
            "method":"GET",
            "url": `https://api.spoonacular.com/recipes/${e.target.id}/analyzedInstructions`,
            "headers":{
            "content-type":"application/octet-stream",
            "x-rapidapi-host":"spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
            "x-rapidapi-key":"bb0f200289mshe78ca82c1ce41f8p10d1a9jsn884393783c99"
            },"params":{
            "number":"100"
            }
            })
            .then(res => setRecipeDetails(() => {return[
                ...res.data]}
            ))
            .catch(err => console.log(err))
        }


    return ( 
        <>
        {recipeList.map((item) => 
            <div className = "main-home"> 
                <img alt = "image not found" src ={`https://spoonacular.com/recipeImages/${item.id}-636x393.jpg`}></img>
                <h1 className = "recp-title">{item.title}</h1>
                <h4 className = "recp-sub-title">Ready in {item.readyInMinutes} minutes</h4>
                <h4 className = "recp-sub-title">Serves {item.servings}</h4>
                <form className = "button" >
                <button to = "/details" onClick = {(e) => {
                    getDetails(e) 
                    props.history.push(`/details/${item.id}`)
                    }}>See More</button>
                    </form>
                    <Switch>
                        <Route exact path = {`/details/${item.id}`}>
                            <Recipedetails recipeDetails = {recipeDetails}/>
                        </Route>
                </Switch>
            </div>
                )}
        </>

    )
}



export default Recipieslist
