import React, {useState} from 'react'


function Authform(){ 

    const initialInputs = { 
        username: "", 
        password: ""
    }

    const {login, setLogin } = useState(initialInputs)
    const {toggle, setToggle} = useState(false)


    function handleChange(e){ 
       const {name, value} = e.target
       setLogin(prev => ({ 
           ...prev,
           [name]: value
       }))
    }

    return( 
        <div> 
            {toggle ? 
            <>
                <input 
                type = "text"
                placeholder = "Username"
                value = {login}
                name = "username"
                onChange = {handleChange}/>
                <input 
                type = "text"
                placeholder = "Password"
                value = {login}
                name = "password"
                onChange = {handleChange}/>
                <button>Login</button>
                <div>Not a member? </div>
                </>
            : 
            <>
            <input 
                type = "text"
                placeholder = "Username"
                value = {login}
                name = "username"
                onChange = {handleChange}/>
                <input 
                type = "text"
                placeholder = "Password"
                value = {login}
                name = "password"
                onChange = {handleChange}/>
                <button>Sign Up</button>
            </>
            }

        </div>
    )
}

export default Authform
