import React, { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"





function Recipedetails(props){ 
    let {id} = useParams()
    const [recipeDetails, setRecipeDetails] = useState({})

   async function getDetails(){ 
        await axios({
            "method":"GET",
            "url": `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=false&apiKey=3cada9b92e194b29af52f3e43a75c988`,
            "headers":{
            "content-type":"application/json",
            // "x-rapidapi-host":"spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
            // "x-rapidapi-key":"bb0f200289mshe78ca82c1ce41f8p10d1a9jsn884393783c99"
            },"params":{
            "number":"1"
            }
            })
            .then(res => setRecipeDetails(() => ({
                recipeDetails: res
            })))
            .catch(err => console.log(err))
    }


    useEffect(() => { 
        getDetails()
    }, [])

    return( 
        <div> 
            <h1>{recipeDetails && recipeDetails.data.title}</h1>
            <img src = {`https://spoonacular.com/recipeImages/${recipeDetails.data.id}-636x393.jpg`}></img>
            <h4>ready in {recipeDetails && recipeDetails.data.readyInMinutes}</h4>
            <h4>serves {recipeDetails && recipeDetails.data.servings}</h4>
            <p>{recipeDetails && recipeDetails.data.extendedIngredients.map(ingredient => <><img src = {`https://spoonacular.com/cdn/ingredients_100x100/${ingredient.image}`}></img><span>{ingredient.original}</span></>)}</p>
            <p>{recipeDetails && recipeDetails.data.analyzedInstrutions.steps.map(step => <p>step {step.number} {step.step}</p>)}</p>

        </div> 

    )
}


export default Recipedetails
