import React from "react"


function RandomRecipes(props){ 
        const {recipe} = props
        const boldTitle = { 
            fontWeight: 'bold', 
            marginBottom: '10pt', 
            paddingBottom: '8pt'
        }
        let i = 1
        console.log(props)
    return ( 
        <>
        {recipe.recipe && recipe.recipe.recipes.map(recipe => 
            <div className ="main-home"> 
                    <h1 className = "rand-title">{recipe.title}</h1>
                    <span className = "subtitle">Ready in {recipe.readyInMinutes}</span>
                    <span className = "subtitle">Serves {recipe.servings}</span>
                    <img src = {recipe.image}></img>
                    <span className = "recipe-content2" style = {boldTitle}>Ingredients <hr width = "30%"></hr></span>
                    {recipe.extendedIngredients.map(ingredient => <ul className = "recipe-content4">{ingredient}</ul>)}
                    <span className = "recipe-content2" style = {boldTitle}>Instructions  <hr width = "30%"></hr>
                    {recipe.analyzedInstructions[0].steps.map(step => <p className = "recipe-content3"><b>step {i}{i++,"."}</b>  {step}</p>)}
                    </span> 
            </div>
                    )}
        </>

    )
}

export default RandomRecipes
